<?phpadd_action( 'wp_enqueue_scripts', 'theme_enqueue_styles' );function theme_enqueue_styles() {    wp_enqueue_style( 'parent-style', get_template_directory_uri() . '/style.css' );}	//======================================================================// CUSTOM DASHBOARD//======================================================================// ADMIN FOOTER TEXTfunction remove_footer_admin () {    echo "Divi Child Theme";} // Fetch image alt text from media libraryfunction get_image_alt_text($image_url) {if ( ! $image_url ) return '';if ( '/' === $image_url[0] )$post_id = attachment_url_to_postid(home_url() . $image_url);else$post_id = attachment_url_to_postid($image_url);$alt_text = get_post_meta($post_id, '_wp_attachment_image_alt', true);if ( '' === $alt_text )$alt_text = get_the_title($post_id);return $alt_text;}/* Update image alt text in module properties */function update_module_alt_text( $attrs, $unprocessed_attrs, $slug ) {if ( ( $slug === 'et_pb_image' || $slug === 'et_pb_fullwidth_image' ) && '' === $attrs['alt'] )$attrs['alt'] = get_image_alt_text($attrs['src']);elseif ( $slug === 'et_pb_blurb' && 'off' === $attrs['use_icon'] && '' === $attrs['alt'] )$attrs['alt'] = get_image_alt_text($attrs['image']);elseif ( $slug === 'et_pb_slide' && '' !== $attrs['image'] && '' === $attrs['image_alt'] )$attrs['image_alt'] = get_image_alt_text($attrs['image']);elseif ( $slug === 'et_pb_fullwidth_header' ) {if ( '' !== $attrs['logo_image_url'] && '' === $attrs['logo_alt_text'] )$attrs['logo_alt_text'] = get_image_alt_text($attrs['logo_image_url']);if ( '' !== $attrs['header_image_url'] && '' === $attrs['image_alt_text'] )$attrs['image_alt_text'] = get_image_alt_text($attrs['header_image_url']);}return $attrs;}/* Filter injection */add_filter( 'et_pb_module_shortcode_attributes', 'update_module_alt_text', 20, 3 );add_filter('admin_footer_text', 'remove_footer_admin');// PÃ¡gina aleatoriaadd_action('init','random_post');function random_post() {       global $wp;       $wp->add_query_var('random');       add_rewrite_rule('random/?$', 'index.php?random=1', 'top');} add_action('template_redirect','random_template');function random_template() {       if (get_query_var('random') == 1) {               $posts = get_posts('post_type=page&orderby=rand&numberposts=1&exclude=[1526]');               foreach($posts as $post) {                       $link = get_permalink($post);               }               wp_redirect($link,307);               exit;       }}